mapper:


	select *
	from housedeal h right outer join houseinfo i on h.aptname=i.aptname
	where h.dong = #{dongcode} and i.dong= #{dongcode}



	select * 
	from housedeal h left outer join houseinfo i on h.AptName=i.AptName 
	where h.aptname like concat('%', #{aptName}, '%') limit 6



	SELECT *, (6371*acos(cos(radians(lat))*cos(radians(#{lat}))*cos(radians(#{lng})-radians(lit))+sin(radians(lat))*sin(radians(#{lat})))) AS distance 
	FROM parkinfo HAVING distance <= 1 ORDER BY distance LIMIT 20;



	SELECT *, (6371*acos(cos(radians(lat))*cos(radians(#{lat}))*cos(radians(#{lng})-radians(lit))+sin(radians(lat))*sin(radians(#{lat})))) AS distance 
	FROM market HAVING distance <= 1 ORDER BY distance LIMIT 20;



	SELECT *, (6371*acos(cos(radians(lat))*cos(radians(#{lat}))*cos(radians(#{lng})-radians(lit))+sin(radians(lat))*sin(radians(#{lat})))) AS distance 
	FROM busstop HAVING distance <= 100 ORDER BY distance LIMIT 20;



	SELECT *, (6371*acos(cos(radians(lat))*cos(radians(#{lat}))*cos(radians(#{lng})-radians(lit))+sin(radians(lat))*sin(radians(#{lat})))) AS distance 
	FROM policeinfo HAVING distance <= 2 ORDER BY distance LIMIT 20;



	SELECT * from houseinfo where no = #{no}


